import React from 'react';
import { Box, Typography, Container, Chip } from '@mui/material';
import { usePdf } from '../contexts/PdfContext';

const Footer = () => {
  const { totalTokensUsed } = usePdf();

  return (
    <Box
      component="footer"
      sx={{
        py: 3,
        px: 2,
        mt: 'auto',
        backgroundColor: (theme) => theme.palette.primary.main,
        color: 'white',
      }}
    >
      <Container maxWidth="lg">
        <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start' }}>
          <Chip 
            label={`Tokens consumidos en esta sesión: ${totalTokensUsed.toLocaleString()}`} 
            color="secondary"
            size="small"
            sx={{
              mb: 1,
              color: 'white',
              fontWeight: 'bold',
              background: 'rgba(255, 255, 255, 0.2)',
              border: '1px solid rgba(255, 255, 255, 0.3)',
              '& .MuiChip-label': {
                overflow: 'visible'
              }
            }}
          />

          <Typography variant="body2" gutterBottom>
            (U)Boost © Abstract Scientific Paper - Marzo 2025
          </Typography>
          <Typography variant="caption" display="block" sx={{ color: 'rgba(255, 255, 255, 0.7)' }}>
            Licencia MIT - Software de código abierto para uso educativo y académico
          </Typography>
        </Box>
      </Container>
    </Box>
  );
};

export default Footer;
