import React, { useState } from 'react';
import { 
  TextField, 
  Button, 
  Box, 
  Typography, 
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Alert,
  Grid,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Slider,
  Checkbox,
  FormControlLabel
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import SummarizeIcon from '@mui/icons-material/Summarize';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import TuneIcon from '@mui/icons-material/Tune';
import { usePdf } from '../contexts/PdfContext';

const QueryForm = ({ isSimpleQuery }) => {
  const { 
    processQuery, 
    generateSummary, 
    selectedPdfs,
    queryLoading,
    summaryLoading
  } = usePdf();

  const [query, setQuery] = useState('');
  const [language, setLanguage] = useState('Español');
  const [model, setModel] = useState('gpt-4o-mini');
  const [error, setError] = useState(null);

  // Parámetros avanzados para OpenAI
  const [maxTokens, setMaxTokens] = useState(4000);
  const [useMaxTokens, setUseMaxTokens] = useState(true);
  const [temperature, setTemperature] = useState(0.7);
  const [topP, setTopP] = useState(1);
  const [frequencyPenalty, setFrequencyPenalty] = useState(0);
  const [presencePenalty, setPresencePenalty] = useState(0);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    if (!query.trim()) {
      setError('Por favor ingresa una consulta');
      return;
    }

    try {
      if (isSimpleQuery) {
        await processQuery(query);
      } else {
        // Crear objeto con parámetros avanzados
        const modelParams = {
          model: model,
          temperature: temperature,
          top_p: topP,
          frequency_penalty: frequencyPenalty,
          presence_penalty: presencePenalty
        };

        // Solo añadir max_tokens si no se usa el máximo por defecto
        if (!useMaxTokens) {
          modelParams.max_tokens = maxTokens;
        }

        await generateSummary(query, language, modelParams);
      }
    } catch (err) {
      setError('Error al procesar la consulta: ' + err.message);
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
      {error && (
        <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError(null)}>
          {error}
        </Alert>
      )}

      {selectedPdfs.length === 0 && (
        <Alert severity="info" sx={{ mb: 2 }}>
          No has seleccionado ningún PDF. Se buscarán en todos los documentos disponibles.
        </Alert>
      )}

      <Typography variant="h6" gutterBottom>
        {isSimpleQuery ? 'CONSULTA SIMPLE' : 'GENERAR RESUMEN ESTRUCTURADO'}
      </Typography>

      <TextField
        fullWidth
        id="query"
        label={isSimpleQuery ? "Escribe tu pregunta" : "Describe qué tipo de resumen necesitas"}
        placeholder={isSimpleQuery 
          ? "Ej: ¿Cuáles son los principales hallazgos del estudio?" 
          : "Ej: Necesito un resumen detallado sobre la metodología y resultados del estudio"}
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        multiline
        rows={3}
        sx={{ mb: 2 }}
        variant="outlined"
      />



      {!isSimpleQuery && (
        <React.Fragment>
          <Grid container spacing={2} sx={{ mb: 2 }}>
            <Grid item xs={12} sm={6} md={4}>
              <FormControl fullWidth variant="outlined" size="small">
                <InputLabel id="language-select-label">Idioma</InputLabel>
                <Select
                  labelId="language-select-label"
                  id="language"
                  value={language}
                  onChange={(e) => setLanguage(e.target.value)}
                  label="Idioma"
                >
                  <MenuItem value="Español">Español</MenuItem>
                  <MenuItem value="English">English</MenuItem>
                  <MenuItem value="Français">Français</MenuItem>
                  <MenuItem value="Deutsch">Deutsch</MenuItem>
                  <MenuItem value="Italiano">Italiano</MenuItem>
                  <MenuItem value="Português">Português</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6} md={8}>
              <FormControl fullWidth variant="outlined" size="small">
                <InputLabel id="model-select-label">Modelo</InputLabel>
                <Select
                  labelId="model-select-label"
                  id="model"
                  value={model}
                  onChange={(e) => setModel(e.target.value)}
                  label="Modelo"
                >
                  <MenuItem value="gpt-4o-mini">GPT-4o-mini (Rápido)</MenuItem>
                  <MenuItem value="gpt-4o">GPT-4o (Avanzado)</MenuItem>
                </Select>
              </FormControl>
            </Grid>
          </Grid>

          <Accordion sx={{ mt: 2, mb: 2, boxShadow: 'none', border: '1px solid rgba(0, 0, 0, 0.12)', borderRadius: '4px' }}>
            <AccordionSummary
              expandIcon={<ExpandMoreIcon />}
              aria-controls="panel1a-content"
              id="panel1a-header"
              sx={{ backgroundColor: 'rgba(0, 0, 0, 0.02)' }}
            >
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <TuneIcon sx={{ mr: 1, fontSize: '0.9rem', color: 'text.secondary' }} />
                <Typography variant="body2">Opciones avanzadas</Typography>
              </Box>
            </AccordionSummary>
            <AccordionDetails>
              <Grid container spacing={2}>
                {/* Control de tokens */}
                <Grid item xs={12}>
                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={useMaxTokens}
                        onChange={(e) => setUseMaxTokens(e.target.checked)}
                      />
                    }
                    label="Usar máximo de tokens disponibles"
                  />
                </Grid>

                {!useMaxTokens && (
                  <Grid item xs={12}>
                    <Typography id="tokens-slider" gutterBottom variant="body2">
                      Número máximo de tokens: {maxTokens}
                    </Typography>
                    <Slider
                      disabled={useMaxTokens}
                      value={maxTokens}
                      onChange={(e, newValue) => setMaxTokens(newValue)}
                      min={100}
                      max={8000}
                      step={100}
                      valueLabelDisplay="auto"
                      aria-labelledby="tokens-slider"
                    />
                  </Grid>
                )}

                {/* Temperature */}
                <Grid item xs={12} sm={6}>
                  <Typography id="temperature-slider" gutterBottom variant="body2">
                    Temperature: {temperature}
                  </Typography>
                  <Slider
                    value={temperature}
                    onChange={(e, newValue) => setTemperature(newValue)}
                    min={0}
                    max={2}
                    step={0.1}
                    valueLabelDisplay="auto"
                    aria-labelledby="temperature-slider"
                  />
                  <Typography variant="caption" color="text.secondary">
                    Valores más altos = más creatividad, valores más bajos = más precisión
                  </Typography>
                </Grid>

                {/* Top P */}
                <Grid item xs={12} sm={6}>
                  <Typography id="top-p-slider" gutterBottom variant="body2">
                    Top P: {topP}
                  </Typography>
                  <Slider
                    value={topP}
                    onChange={(e, newValue) => setTopP(newValue)}
                    min={0}
                    max={1}
                    step={0.05}
                    valueLabelDisplay="auto"
                    aria-labelledby="top-p-slider"
                  />
                  <Typography variant="caption" color="text.secondary">
                    Controla la diversidad de palabras (1 = considerar todas las palabras)
                  </Typography>
                </Grid>

                {/* Frequency Penalty */}
                <Grid item xs={12} sm={6}>
                  <Typography id="frequency-penalty-slider" gutterBottom variant="body2">
                    Frequency Penalty: {frequencyPenalty}
                  </Typography>
                  <Slider
                    value={frequencyPenalty}
                    onChange={(e, newValue) => setFrequencyPenalty(newValue)}
                    min={-2}
                    max={2}
                    step={0.1}
                    valueLabelDisplay="auto"
                    aria-labelledby="frequency-penalty-slider"
                  />
                  <Typography variant="caption" color="text.secondary">
                    Reduce la repetición de palabras frecuentes
                  </Typography>
                </Grid>

                {/* Presence Penalty */}
                <Grid item xs={12} sm={6}>
                  <Typography id="presence-penalty-slider" gutterBottom variant="body2">
                    Presence Penalty: {presencePenalty}
                  </Typography>
                  <Slider
                    value={presencePenalty}
                    onChange={(e, newValue) => setPresencePenalty(newValue)}
                    min={-2}
                    max={2}
                    step={0.1}
                    valueLabelDisplay="auto"
                    aria-labelledby="presence-penalty-slider"
                  />
                  <Typography variant="caption" color="text.secondary">
                    Incentiva hablar de nuevos temas
                  </Typography>
                </Grid>
              </Grid>
            </AccordionDetails>
          </Accordion>
        </React.Fragment>
      )}

      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 1, mb: 2 }}>
        <Button
          type="submit"
          variant="contained"
          size="medium"
          sx={{ 
            px: 4, 
            py: 1, 
            borderRadius: 2,
            maxWidth: '300px',
            textTransform: 'none',
            boxShadow: 2
          }}
          startIcon={isSimpleQuery ? <SearchIcon /> : <SummarizeIcon />}
          disabled={queryLoading || summaryLoading}
        >
          {isSimpleQuery 
            ? 'Buscar' 
            : 'Generar Resumen'}
        </Button>
      </Box>

      {!isSimpleQuery && (
        <Typography variant="body2" color="text.secondary" sx={{ mt: 2 }}>
          El resumen estructurado incluirá título, contexto, metodología, resultados, referencias, variables empleadas y otros elementos relevantes. La generación puede tardar hasta dos minutos dependiendo del tamaño de los documentos y el modelo seleccionado de OpenAI.
        </Typography>
      )}
    </Box>
  );
};

export default QueryForm;
