import React from 'react';
import { Box, Typography, Container, Chip, Stack } from '@mui/material';
import { usePdf } from '../contexts/PdfContext';

const Footer = () => {
  const { totalTokensUsed } = usePdf();

  return (
    <Box
      component="footer"
      sx={{
        py: 3,
        px: 2,
        mt: 'auto',
        backgroundColor: (theme) => theme.palette.primary.main,
        color: 'white',
      }}
    >
      <Container maxWidth="lg">
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Box>
            <Typography variant="body2" gutterBottom>
              (U)Boost © Abstract Scientific Paper - Marzo 2025
            </Typography>
            <Typography variant="caption" display="block" sx={{ color: 'rgba(255, 255, 255, 0.7)' }}>
              Licencia MIT - Software de código abierto para uso educativo y académico
            </Typography>
          </Box>

          <Stack direction="column" spacing={1} alignItems="flex-end">
            <Chip 
              label={`Tokens consumidos: ${totalTokensUsed.toLocaleString()}`} 
              color="secondary"
              size="small"
              sx={{
                color: 'white',
                fontWeight: 'bold',
                background: 'rgba(255, 255, 255, 0.15)',
                border: '1px solid rgba(255, 255, 255, 0.3)',
                '& .MuiChip-label': {
                  overflow: 'visible'
                }
              }}
            />
          </Stack>
        </Box>
      </Container>
    </Box>
  );
};

export default Footer;
